ewComponent, rPPCCode, platformPowerPC	}};resource 'STR ' (rNameResource) {	"JPEGView JFIF Preview Component";};resource 'STR ' (rInfoResource) {	"Displays and extracts JFIF preview images (through version 1.01 of the JFIF spec)";};