groundAndForeground,	dontGetFrontClicks,	ignoreAppDiedEvents,	is32BitCompatible,	isHighLevelEventAware,	localAndRemoteHLEvents,	notStationeryAware,	useTextEditServices,	reserved,	reserved,	reserved,		1100 * 1024,	800 * 1024};resource 'SIZE' (-1){	reserved, 	acceptSuspendResumeEvents,	reserved,	canBackground,	doesActivateOnFGSwitch,	backgroundAndForeground,	getFrontClicks,	ignoreAppDiedEvents,	is32BitCompatible,	isHighLevelEventAware,	localAndRemoteHLEvents,	notStationeryAware,	useTextEditServices,	reserved,	reserved,	reserved,		800 * 1024,	800 * 1024};/***********************************************************************//************************                      *************************//************************  FREF / BNDL / JVWR  *************************//************************                      *************************//***********************************************************************/resource 'FREF' (rAPPLType) {	kAPPLType, rAPPLID, ""};resource 'FREF' (rPICTType) {	kPICTType, rPICTID, ""};resource 'FREF' (rJFIFType) {	kJFIFType, rJFIFID, ""};resource 'FREF' (rJPEGType) {	kJPEGType, rJPEGID, ""};resource 'FREF' (rHelpType) {	kHelpType, rHelpID, ""};resource 'FREF' (rGIFType) {	kGIFType, rGIFID, ""};resource 'FREF' (rAllTypes) {	kAllTypes, rAllID, ""};resource 'FREF' (rTIFFType) {	kTIFFType, rTIFFID, ""};resource 'FREF' (rBMPType) {	kBMPType, rBMPID, ""};resource 'FREF' (rSCRNType) {	kSCRNType, rSCRNID, ""};resource 'FREF' (rPNTGType) {	kPNTGType, rPNTGID, ""};resource 'BNDL' (rBNDLRsrc) {	kCreator, rJVWRRsrc,	{		'FREF',		{			rAPPLID, rAPPLType,			rPICTID, rPICTType,			rJFIFID, rJFIFType,			rJPEGID, rJPEGType,			rHelpID, rHelpType,			rGIFID, rGIFType,			rAllID, rAllTypes,			rTIFFID, rTIFFType,			rBMPID, rBMPType,			rSCRNID, rSCRNType,			rPNTGID, rPNTGType		},		'ICN#',		{			rAPPLID, rAPPLIcon,			rPICTID, rPICTIcon,			rJFIFID, rJPEGIcon,			rJPEGID, rJPEGIcon,			rHelpID, rHelpIcon,			rGIFID, rGIFIcon,			rAllID, rOtherIcon,			rTIFFID, rTIFFIcon,			rBMPID, rBMPIcon,			rSCRNID, rSCRNIcon,			rPNTGID, rPNTGIcon		}	}};type kCreator as 'STR ';resource kCreator (rJVWRRsrc) {	"JPEGView Version 3.2"};/***********************************************************************//************************                      *************************//************************     Finder Help      *************************//************************                      *************************//***********************************************************************/resource 'hfdr' (-5696, purgeable) {	HelpMgrVersion, hmDefaultOptions, 0, 0,	{		HMStringItem {			"JPEGView is a nifty utility that can quickly view JPEG, GIF, TIFF, "				"PICT, MacPaint, and Startup Screen files.  It even comes with "				"a slide show and extensive on-line help!"		}	}};/***********************************************************************//*******************                              **********************//*******************       Statistics Window      **********************//*******************                              **********************//***********************************************************************/resource 'WIND' (rStatWindow, preload){	{ 0, 0, 100, 100 },	windoidZoomProc, invisible, goAway, kStatWindowID, "Statistics",	noAutoCenter};/***********************************************************************//*******************                              **********************//*******************        Comments window       **********************//*******************                              **********************//***********************************************************************/resource 'WIND' (rCommentsWindow, preload){	{ 0, 0, 100, 100 },	windoidProc, invisible, goAway, kCommentsWindowID, "Comments",	noAutoCenter};/***********************************************************************//*******************                              **********************//*******************         Colors window        **********************//*******************                              **********************//***********************************************************************/resource 'WIND' (rColorsWindow, preload){	{ 0, 0, 49, 257 },	windoidProc, invisible, goAway, kColorsWindowID, "Colors",	noAutoCenter};resource 'wctb' (rColorsWindow, preload) {	{ }, { }};/***********************************************************************//*******************                              **********************//*******************         Image windows        **********************//*******************                              **********************//***********************************************************************/resource 'WIND' (rImageWindow, preload){	{ 0, 0, 100, 100 },	zoomNoGrow, invisible, goAway, kImageWindowID, "",	noAutoCenter};resource 'wctb' (rImageWindow, preload) {	{		wContentColor, 0x0000, 0x0000, 0x0000,		wFrameColor, 0x1111, 0x1111, 0x1111	}, { }};resource 'WIND' (rFullImageWindow, preload){	{ 0, 0, 100, 100 },	plainDBox, invisible, goAway, kImageWindowID, "",	noAutoCenter};resource 'wctb' (rFullImageWindow, preload) {	{		wContentColor, 0x0000, 0x0000, 0x0000,		wFrameColor, 0x1111, 0x1111, 0x1111	}, { }};resource 'WIND' (rAboutImageWindow, preload){	{ 0, 0, 100, 100 },	noGrowDocProc, invisible, goAway, kImageWindowID, "",	noAutoCenter};resource 'wctb' (rAboutImageWindow, preload) {	{		wContentColor, 0x0000, 0x0000, 0x0000,		wFrameColor, 0x1111, 0x1111, 0x1111	}, { }};/***********************************************************************//************************                      *************************//************************      Help Window     *************************//************************                      *************************//***********************************************************************/resource 'WIND' (rHelpWindow, preload) {	{ 0, 0, 300, 480 },	zoomDocProc, invisible, goAway, kHelpWindowID, "JPEGView Help",	noAutoCenter};resource 'CNTL' (rHelpMenuControl, preload) {	{ 7, 0, 26, 375 },	popupTitleLeftJust, visible, 80, rHelpMenu, popupMenuCDEFProc, 0, "Help Topic:"};resource 'CNTL' (rHelpScrollControl, preload) {	{ 22, 460, 279, 476 },	0, visible, 0, 0, scrollBarProc, 0, ""};/***********************************************************************//************************                      *************************//************************Slide Controls Window *************************//************************                      *************************//***********************************************************************/resource 'WIND' (rSlideControlsWindow, preload) {	{ 0, 0, 29, 28 * 4 },	windoidProc, invisible, noGoAway, kSlideControlsWindowID, "Slide Show",	noAutoCenter};resource 'wctb' (rSlideControlsWindow, preload) {	{ }, { }};/***********************************************************************//************************                      *************************//************************    Error Messages    *************************//************************                      *************************//***********************************************************************/resource 'STR#' (rFatalErrors, preload) {	{		"Not enough memory to start up!",		"Couldn’t load all the code resources!",		"Couldn’t load the string resources!",		"Couldn’t load the cursors!",		"JPEGView requires System 7.0 or later to run!",		"Couldn’t find the system color tables!",		"Couldn’t load the application menus!",		"Couldn’t install the supported AppleEvents!",		"Unable to identify the active screen devices!"	}};resource 'STR#' (rMinorErrors, pr